import React, { Component } from 'react'
import { Container, Row, Col} from 'react-bootstrap'
import MobileService from './../../../service/mobile.service'

class MobileDetails extends Component {

    constructor() {
        super()
        this.state = {
            mobile: []
        }
        this.mobileService = new MobileService ()
    }

    componentDidMount = () => {

        const mobile_id = this.props.match.params.mobile_id

        this.mobileService
            .getOneMobile(mobile_id)
            .then(res => this.setState({ mobile: res.data }))
            .catch(err => console.log(err))
    }

    render() {

        return (

            <Container>
                <Row>
                    <Col>
                        <img src={this.state.mobile.imageFileName}/>
                    </Col>
                    <Col>
                        <h5>{this.state.mobile.description}</h5>
                    </Col>
                </Row>
            </Container>


        )
    }
}

export default MobileDetails
